pragma solidity >=0.7.6;

import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import {
    ISuperfluid
} from "@superfluid-finance_1/ethereum-contracts/contracts/interfaces/superfluid/ISuperfluid.sol";
import "@superfluid-finance_1/ethereum-contracts/contracts/interfaces/ux/IResolver.sol";

import "./ReactionToken.sol";
import "./StakedFlow.sol";

contract ReactionFactory is Context, UUPSUpgradeable, Initializable {
    using EnumerableSet for EnumerableSet.AddressSet;

    address private _sfHost; // host
    address private _sfCfa; // the stored constant flow agreement class address
    address private _sfSuperTokenFactory;
    address private _sfResolver;
    string private _sfVersion;

    IResolver internal _resolver; // Superfluid resolver

    address owner;

    mapping (address => address) public superTokenRegistry;  // token registry for non-official tokens

    EnumerableSet.AddressSet private superTokensSet; // Registry supertokens

    mapping(address => mapping(address => address)) internal _stakedFlows;

    event Initialized(address sfHost, address sfCfa, address sfSuperTokenFactory, address sfResolver, string sfVersion);
    event ReactionDeployed(address creator, address reactionContractAddr, string reactionTokenName, string reactionTokenSymbol, string tokenMetadataURI, address stakingTokenAddress);

    function initialize(address sfHost, address sfCfa, address sfSuperTokenFactory, address sfResolver, string memory sfVersion) public payable initializer {
        require(address(sfHost) != address(0), "ReactionFactory: Host Address can't be 0x");
        require(address(sfCfa) != address(0), "ReactionFactory: CFA Address can't be 0x");
        require(address(sfSuperTokenFactory) != address(0), "ReactionFactory: SuperTokenFactory Address can't be 0x");
        require(address(sfResolver) != address(0), "ReactionFactory: Resolver Address can't be 0x");

        _sfHost = sfHost;
        _sfCfa = sfCfa;
        _sfSuperTokenFactory = sfSuperTokenFactory;
        _sfResolver = sfResolver;
        _sfVersion = sfVersion;

        _resolver = IResolver(sfResolver);

        owner = _msgSender();

        emit Initialized(_sfHost, _sfCfa, _sfSuperTokenFactory, _sfResolver, _sfVersion);
    }

    function deployReaction(string memory reactionTokenName, string memory reactionTokenSymbol, string memory tokenMetadataURI, address stakingTokenAddress, uint8 monthDistributionPercentage) external returns (address){
        ReactionToken reactionContract = new ReactionToken(
            address(this),
            _sfHost, 
            _sfCfa,
            stakingTokenAddress,
            reactionTokenName, 
            reactionTokenSymbol,
            tokenMetadataURI,
            monthDistributionPercentage
        );

        address reactionContractAddr = address(reactionContract);

        emit ReactionDeployed(_msgSender(), reactionContractAddr, reactionTokenName, reactionTokenSymbol, tokenMetadataURI, stakingTokenAddress);

        return reactionContractAddr;
    }

    function isSuperToken(ERC20WithTokenInfo _token) public view returns (bool) {
        if(!superTokensSet.contains(address(_token))){
            string memory tokenId = string(abi.encodePacked('supertokens', '.', _sfVersion, '.', _token.symbol()));
            return _resolver.get(tokenId) == address(_token);
        }
        return true;
    }

    function getSuperToken(ERC20WithTokenInfo _token) public view returns (address tokenAddress) {
        if(isSuperToken(_token)){
            tokenAddress = address(_token);
        } else {
            string memory tokenId = string(abi.encodePacked('supertokens', '.', _sfVersion, '.', _token.symbol(), 'x'));
            tokenAddress = _resolver.get(tokenId);

            if (tokenAddress == address(0)) { // Look on the App registry if there's already a "non-oficially registered" Supertoken
                tokenAddress = superTokenRegistry[address(_token)];
            }
        }
    }

    function createSuperToken(ERC20WithTokenInfo _token) public returns (ISuperToken superToken) {
        require(isSuperToken(_token) == false, "ReactionFactory: Token is already a SuperToken");

        if (superTokenRegistry[address(_token)] != address(0)) {
            superToken = ISuperToken(superTokenRegistry[address(_token)]);
        } else {
            ISuperTokenFactory factory = ISuperfluid(_sfHost).getSuperTokenFactory();
            string memory name = string(abi.encodePacked('Super ', _token.name()));
            string memory symbol = string(abi.encodePacked(_token.symbol(), 'x'));
            superToken = factory.createERC20Wrapper(_token, ISuperTokenFactory.Upgradability.FULL_UPGRADABE, name, symbol);
            superTokenRegistry[address(_token)] = address(superToken);
            superTokensSet.add(address(superToken));
        }
    }

    function getStakedFlow(address user, address token, uint8 monthDistributionPercentage) public returns (address){
        address stakedFlow = _stakedFlows[user][token];
        if(stakedFlow == address(0)){
            stakedFlow = address(new StakedFlow(address(this), _sfHost, _sfCfa, monthDistributionPercentage));
            _stakedFlows[user][token] = stakedFlow;
        }

        return stakedFlow;
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {
    }

     /* ========== MODIFIERS ========== */

    modifier onlyOwner() {
        require(msg.sender == owner, "ReactionFactory: Caller is not owner");
        _;
    }
}","keccak256":"0x4b88a7e50224d89ff45d11b6766dc203475cc12a06de51f0e833821978f614a6","license":"MIT"},"src/reaction/ReactionToken.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.7.6;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableMap.sol";

import {
    IConstantFlowAgreementV1
} from "@superfluid-finance_1/ethereum-contracts/contracts/interfaces/agreements/IConstantFlowAgreementV1.sol";
import {
    ISuperfluid,
    ISuperToken,
    ISuperTokenFactory
} from "@superfluid-finance_1/ethereum-contracts/contracts/interfaces/superfluid/ISuperfluid.sol";

import "./ReactionFactory.sol";
import "./StakedFlow.sol";

contract ReactionToken is Context, ERC20 {
    event Staked(address author, uint256 amount, address stakingTokenAddress);
    event Reacted(address author, address reactionRecipientAddress, uint256 tokenId, address reactionTokenAddress, uint256 amount, string reactionTokenName, string reactionTokenSymbol);
    event Flowed(address flow, uint256 amount, address stakingTokenAddress, address recipient, address stakingSuperTokenAddress);

    address private _sfHost; // host
    address private _sfCfa; // the stored constant flow agreement class address

    address private _stakingTokenAddress; // If 0x, all tokens will be accepted to stake
    uint8 private _monthDistributionPercentage; // Monthly percentage to be returned (0-100)

    string internal _tokenMetadataURI; // Metadata url

    ReactionFactory internal _reactionFactory;

    constructor(
        address reactionFactory,
        address sfHost, 
        address sfCfa, 
        address stakingTokenAddress,
        string memory reactionTokenName, 
        string memory reactionTokenSymbol,
        string memory tokenMetadataURI,
        uint8 monthDistributionPercentage
    ) ERC20(reactionTokenName, reactionTokenSymbol) {
        require(address(reactionFactory) != address(0), "ReactionToken: Reaction Factory can't be 0x");
        require(address(sfHost) != address(0), "ReactionToken: Host Address can't be 0x");
        require(address(sfCfa) != address(0), "ReactionToken: CFA Address can't be 0x");
        require(monthDistributionPercentage < 101, "ReactionToken: monthDistributionPercentage must be between 0 and 100");
        require(monthDistributionPercentage > 0, "ReactionToken: monthDistributionPercentage must be between 0 and 100");

        _reactionFactory = ReactionFactory(reactionFactory);
        _sfHost = sfHost;
        _sfCfa = sfCfa;

        _stakingTokenAddress = stakingTokenAddress;
        _monthDistributionPercentage = monthDistributionPercentage;
        _tokenMetadataURI = tokenMetadataURI;
    }

    function stakeAndMint(uint256 amount, address stakingTokenAddress, address reactionRecipientAddress, uint256 tokenId) public {
        require(address(stakingTokenAddress) != address(0), "ReactionToken: Staking Token Address can't be 0x");
        require(address(reactionRecipientAddress) != address(0), "ReactionToken: reactionRecipient Address can't be 0x");

        // Verify if the staking token is correct
        if(_stakingTokenAddress != address(0)){
            require(address(stakingTokenAddress) == address(_stakingTokenAddress), "ReactionToken: Invalid Staking Token");
        }

        // Stake everything to the StakedFlow
        StakedFlow stakedFlow = StakedFlow(_reactionFactory.getStakedFlow(_msgSender(), stakingTokenAddress, _monthDistributionPercentage));

        IERC20(stakingTokenAddress).transferFrom(_msgSender(), address(stakedFlow), amount);
        emit Staked(_msgSender(), amount, stakingTokenAddress);

        // Mint the reaction token straight to the Recipient
        _mint(reactionRecipientAddress, amount);
        ERC20 reactionToken = ERC20(address(this));
        emit Reacted(_msgSender(), reactionRecipientAddress, tokenId, address(this), amount, reactionToken.name(), reactionToken.symbol());

        // Upsert the stream the staked amount
        address stakingSuperTokenAddress = stakedFlow.flow(amount, stakingTokenAddress, _msgSender());
        emit Flowed(address(stakedFlow), amount, stakingTokenAddress, _msgSender(), stakingSuperTokenAddress); 
    }

    function getTokenMetadataURI() public view returns (string memory) {
        return _tokenMetadataURI;
    }

    function getStakingTokenAddress() public view returns (address) {
        return _stakingTokenAddress;
    }
}","keccak256":"0x9642705ee254fdc7ffac20d28c9532d4885acd950e2fc42cf19e8dbf6eff0c26","license":"MIT"},"src/reaction/StakedFlow.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.7.6;

import "@openzeppelin/contracts/utils/Context.sol";

import {
    IConstantFlowAgreementV1
} from "@superfluid-finance_1/ethereum-contracts/contracts/interfaces/agreements/IConstantFlowAgreementV1.sol";
import {
    ISuperfluid,
    ISuperToken,
    ISuperTokenFactory
} from "@superfluid-finance_1/ethereum-contracts/contracts/interfaces/superfluid/ISuperfluid.sol";
import { ERC20WithTokenInfo } from "@superfluid-finance_1/ethereum-contracts/contracts/interfaces/tokens/ERC20WithTokenInfo.sol";

import "./ReactionFactory.sol"; 

contract StakedFlow is Context {
    event Flowing(address stakingSuperToken, uint256 balance, address recipient, uint256 flowRate);

    ISuperfluid internal _host; // Superfluid host address
    IConstantFlowAgreementV1 internal _cfa; // Superfluid Constant Flow Agreement address
    ISuperTokenFactory internal _superTokenFactory; // Superfluid Supertoken Factory

    ReactionFactory internal _reactionFactory;

    uint8 internal _monthDistributionPercentage;

    constructor(
        address reactionFactory,
        address host, 
        address cfa,
        uint8 monthDistributionPercentage
    ) {
        require(address(reactionFactory) != address(0), "StakedFlow: Reaction Factory can't be 0x");
        require(address(host) != address(0), "StakedFlow: Host Address can't be 0x");
        require(address(cfa) != address(0), "StakedFlow: CFA Address can't be 0x");
        require(monthDistributionPercentage < 101, "StakedFlow: monthDistributionPercentage must be between 0 and 100");
        require(monthDistributionPercentage > 0, "StakedFlow: monthDistributionPercentage must be between 0 and 100");

        _reactionFactory = ReactionFactory(reactionFactory);
        _host = ISuperfluid(host);
        _cfa =  IConstantFlowAgreementV1(cfa);
        _monthDistributionPercentage = monthDistributionPercentage;
    }

    function flow(uint256 amount, address stakingTokenAddress, address recipient) public returns (address){
        require(address(stakingTokenAddress) != address(0), "StakedFlow: Staking Token Address can't be 0x");

        ERC20WithTokenInfo stakingToken = ERC20WithTokenInfo(stakingTokenAddress);

        // Get/Create the super token
        address stakingSuperToken = _reactionFactory.isSuperToken(stakingToken) ? address(stakingToken) : _reactionFactory.getSuperToken(stakingToken);
        if (stakingSuperToken == address(0)) {
            stakingSuperToken = address(_reactionFactory.createSuperToken(stakingToken));
        }

        // Approve token to be upgraded
        if (stakingToken.allowance(address(this), stakingSuperToken) < amount) {
            bool success = stakingToken.approve(stakingSuperToken, amount); // max allowance
            require(success, "ReactionToken: Failed to approve allowance to SuperToken");
        }

        // Give token Superpowers
        if(address(stakingToken) != address(stakingSuperToken)){
            ISuperToken(stakingSuperToken).upgrade(amount);
        }

        // Calculate the flow rate
        uint256 secondsInAMonth = 2592000;
        uint256 balance = ISuperToken(stakingSuperToken).balanceOf(address(this));
        uint256 flowRate = (balance*_monthDistributionPercentage)/(secondsInAMonth*100);

        // Create/Uodate CFA
        (, int96 outFlowRate,,) = _cfa.getFlow(ISuperToken(stakingSuperToken), address(this), recipient);
        if(outFlowRate > 0){
            _host.callAgreement(
                _cfa,
                abi.encodeWithSelector(
                    _cfa.updateFlow.selector,
                    stakingSuperToken,
                    recipient,
                    flowRate,
                    new bytes(0) // placeholder
                ),
                new bytes(0)
            );
        }else{
            _host.callAgreement(
                _cfa,
                abi.encodeWithSelector(
                    _cfa.createFlow.selector,
                    stakingSuperToken,
                    recipient,
                    flowRate,
                    new bytes(0) // placeholder
                ),
                new bytes(0)
            );
        }

        emit Flowing(stakingSuperToken, balance, recipient, flowRate);

        return stakingSuperToken;
    }
